let slideIndex=1;function plusSlides(l){showSlides(slideIndex+=l)}function currentSlide(l){showSlides(slideIndex=l)}function showSlides(l){let e,t=document.getElementsById("mySlides"),s=document.getElementsById("dot");for(l>t.length&&(slideIndex=1),l<1&&(slideIndex=t.length),e=0;e<t.length;e++)t[e].style.display="none";for(e=0;e<s.length;e++)s[e].className=s[e].className.replace(" active","");t[slideIndex-1].style.display="block",s[slideIndex-1].className+=" active"}showSlides(slideIndex);